@SuppressWarnings("unchecked")
public class CopyrightTransfer extends Contract {
    public static final String[] BINARY_ARRAY = {"","6000816000905550600101610fd7565b5090565b905600a165627a7a72305820b8d929f54703367093a0e08caf283cf0f0dcb5cefb554e77239acca6a0e33db00029"};

    public static final String BINARY = org.fisco.bcos.sdk.utils.StringUtils.joinAll("", BINARY_ARRAY);

    public static final String[] SM_BINARY_ARRAY = {"","6000816000905550600101610fd7565b5090565b905600a165627a7a72305820aad23f501d0fc90e2fb1bf23b23b862aa1c16450aaf4ac88a277110a73f1b98a0029"};

    public static final String SM_BINARY = org.fisco.bcos.sdk.utils.StringUtils.joinAll("", SM_BINARY_ARRAY);

    public static final String[] ABI_ARRAY = {"[{\"constant\":false,\"inputs\":[{\"name\":\"transferId\",\"type\":\"uint256\"},{\"name\":\"workId\",\"type\":\"uint256\"},{\"name\":\"fileHash\",\"type\":\"string\"},{\"name\":\"to\",\"type\":\"address\"},{\"name\":\"transferType\",\"type\":\"uint8\"}],\"name\":\"createTransfer\",\"outputs\":[],\"payable\":false,\"stateMutability\":\"nonpayable\",\"type\":\"function\"},{\"constant\":true,\"inputs\":[{\"name\":\"workId\",\"type\":\"uint256\"}],\"name\":\"getWorkTransfers\",\"outputs\":[{\"name\":\"\",\"type\":\"uint256[]\"}],\"payable\":false,\"stateMutability\":\"view\",\"type\":\"function\"},{\"constant\":true,\"inputs\":[{\"name\":\"workId\",\"type\":\"uint256\"}],\"name\":\"getCurrentOwner\",\"outputs\":[{\"name\":\"\",\"type\":\"address\"}],\"payable\":false,\"stateMutability\":\"view\",\"type\":\"function\"},{\"constant\":true,\"inputs\":[{\"name\":\"transferId\",\"type\":\"uint256\"}],\"name\":\"getTransfer\",\"outputs\":[{\"name\":\"workId\",\"type\":\"uint256\"},{\"name\":\"fileHash\",\"type\":\"string\"},{\"name\":\"from\",\"type\":\"address\"},{\"name\":\"to\",\"type\":\"address\"},{\"name\":\"transferType\",\"type\":\"uint8\"},{\"name\":\"timestamp\",\"type\":\"uint256\"}],\"payable\":false,\"stateMutability\":\"view\",\"type\":\"function\"},{\"constant\":true,\"inputs\":[{\"name\":\"workId\",\"type\":\"uint256\"}],\"name\":\"getCurrentRightType\",\"outputs\":[{\"name\":\"\",\"type\":\"uint8\"}],\"payable\":false,\"stateMutability\":\"view\",\"type\":\"function\"}]"};

    public static final String ABI = org.fisco.bcos.sdk.utils.StringUtils.joinAll("", ABI_ARRAY);

    public static final String FUNC_CREATETRANSFER = "createTransfer";

    public static final String FUNC_GETWORKTRANSFERS = "getWorkTransfers";

    public static final String FUNC_GETCURRENTOWNER = "getCurrentOwner";

    public static final String FUNC_GETTRANSFER = "getTransfer";

    public static final String FUNC_GETCURRENTRIGHTTYPE = "getCurrentRightType";

    protected CopyrightTransfer(String contractAddress, Client client, CryptoKeyPair credential) {
        super(getBinary(client.getCryptoSuite()), contractAddress, client, credential);
    }

    public static String getBinary(CryptoSuite cryptoSuite) {
        return (cryptoSuite.getCryptoTypeConfig() == CryptoType.ECDSA_TYPE ? BINARY : SM_BINARY);
    }

    public TransactionReceipt createTransfer(BigInteger transferId, BigInteger workId, String fileHash, String to, BigInteger transferType) {
        final Function function = new Function(
                FUNC_CREATETRANSFER,
                Arrays.<Type>asList(new org.fisco.bcos.sdk.abi.datatypes.generated.Uint256(transferId),
                new org.fisco.bcos.sdk.abi.datatypes.generated.Uint256(workId),
                new org.fisco.bcos.sdk.abi.datatypes.Utf8String(fileHash),
                new org.fisco.bcos.sdk.abi.datatypes.Address(to),
                new org.fisco.bcos.sdk.abi.datatypes.generated.Uint8(transferType)),
                Collections.<TypeReference<?>>emptyList());
        return executeTransaction(function);
    }

    public byte[] createTransfer(BigInteger transferId, BigInteger workId, String fileHash, String to, BigInteger transferType, TransactionCallback callback) {
        final Function function = new Function(
                FUNC_CREATETRANSFER,
                Arrays.<Type>asList(new org.fisco.bcos.sdk.abi.datatypes.generated.Uint256(transferId),
                new org.fisco.bcos.sdk.abi.datatypes.generated.Uint256(workId),
                new org.fisco.bcos.sdk.abi.datatypes.Utf8String(fileHash),
                new org.fisco.bcos.sdk.abi.datatypes.Address(to),
                new org.fisco.bcos.sdk.abi.datatypes.generated.Uint8(transferType)),
                Collections.<TypeReference<?>>emptyList());
        return asyncExecuteTransaction(function, callback);
    }

    public String getSignedTransactionForCreateTransfer(BigInteger transferId, BigInteger workId, String fileHash, String to, BigInteger transferType) {
        final Function function = new Function(
                FUNC_CREATETRANSFER,
                Arrays.<Type>asList(new org.fisco.bcos.sdk.abi.datatypes.generated.Uint256(transferId),
                new org.fisco.bcos.sdk.abi.datatypes.generated.Uint256(workId),
                new org.fisco.bcos.sdk.abi.datatypes.Utf8String(fileHash),
                new org.fisco.bcos.sdk.abi.datatypes.Address(to),
                new org.fisco.bcos.sdk.abi.datatypes.generated.Uint8(transferType)),
                Collections.<TypeReference<?>>emptyList());
        return createSignedTransaction(function);
    }

    public Tuple5<BigInteger, BigInteger, String, String, BigInteger> getCreateTransferInput(TransactionReceipt transactionReceipt) {
        String data = transactionReceipt.getInput().substring(10);
        final Function function = new Function(FUNC_CREATETRANSFER,
                Arrays.<Type>asList(),
                Arrays.<TypeReference<?>>asList(new TypeReference<Uint256>() {}, new TypeReference<Uint256>() {}, new TypeReference<Utf8String>() {}, new TypeReference<Address>() {}, new TypeReference<Uint8>() {}));
        List<Type> results = FunctionReturnDecoder.decode(data, function.getOutputParameters());
        return new Tuple5<BigInteger, BigInteger, String, String, BigInteger>(

                (BigInteger) results.get(0).getValue(),
                (BigInteger) results.get(1).getValue(),
                (String) results.get(2).getValue(),
                (String) results.get(3).getValue(),
                (BigInteger) results.get(4).getValue()
                );
    }

    public List getWorkTransfers(BigInteger workId) throws ContractException {
        final Function function = new Function(FUNC_GETWORKTRANSFERS,
                Arrays.<Type>asList(new org.fisco.bcos.sdk.abi.datatypes.generated.Uint256(workId)),
                Arrays.<TypeReference<?>>asList(new TypeReference<DynamicArray<Uint256>>() {}));
        List<Type> result = (List<Type>) executeCallWithSingleValueReturn(function, List.class);
        return convertToNative(result);
    }

    public String getCurrentOwner(BigInteger workId) throws ContractException {
        final Function function = new Function(FUNC_GETCURRENTOWNER,
                Arrays.<Type>asList(new org.fisco.bcos.sdk.abi.datatypes.generated.Uint256(workId)),
                Arrays.<TypeReference<?>>asList(new TypeReference<Address>() {}));
        return executeCallWithSingleValueReturn(function, String.class);
    }

    public Tuple6<BigInteger, String, String, String, BigInteger, BigInteger> getTransfer(BigInteger transferId) throws ContractException {
        final Function function = new Function(FUNC_GETTRANSFER,
                Arrays.<Type>asList(new org.fisco.bcos.sdk.abi.datatypes.generated.Uint256(transferId)),
                Arrays.<TypeReference<?>>asList(new TypeReference<Uint256>() {}, new TypeReference<Utf8String>() {}, new TypeReference<Address>() {}, new TypeReference<Address>() {}, new TypeReference<Uint8>() {}, new TypeReference<Uint256>() {}));
        List<Type> results = executeCallWithMultipleValueReturn(function);
        return new Tuple6<BigInteger, String, String, String, BigInteger, BigInteger>(
                (BigInteger) results.get(0).getValue(),
                (String) results.get(1).getValue(),
                (String) results.get(2).getValue(),
                (String) results.get(3).getValue(),
                (BigInteger) results.get(4).getValue(),
                (BigInteger) results.get(5).getValue());
    }

    public BigInteger getCurrentRightType(BigInteger workId) throws ContractException {
        final Function function = new Function(FUNC_GETCURRENTRIGHTTYPE,
                Arrays.<Type>asList(new org.fisco.bcos.sdk.abi.datatypes.generated.Uint256(workId)),
                Arrays.<TypeReference<?>>asList(new TypeReference<Uint8>() {}));
        return executeCallWithSingleValueReturn(function, BigInteger.class);
    }

    public static CopyrightTransfer load(String contractAddress, Client client, CryptoKeyPair credential) {
        return new CopyrightTransfer(contractAddress, client, credential);
    }

    public static CopyrightTransfer deploy(Client client, CryptoKeyPair credential) throws ContractException {
        return deploy(CopyrightTransfer.class, client, credential, getBinary(client.getCryptoSuite()), "");
    }
}
